declare class QRCode {
  constructor(
    element: HTMLElement | string,
    options?: {
      text?: string;
      width?: number;
      height?: number;
      colorDark?: string;
      colorLight?: string;
      correctLevel?: number;
    }
  );

  makeCode(text: string): void;
  clear(): void;
}

declare namespace QRCode {
  const CorrectLevel: {
    L: number;
    M: number;
    Q: number;
    H: number;
  };
}
